public class OddEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1=13;
		int num2=2;
		int mod=num1%num2;
		if (mod==0) {
			System.out.println("its even number");
		}
		else
		{
			System.out.println("its odd number");
		}
				
			
		}
	}


